import * as React from 'react';
import BlueLogo from '../images/mlo_blue.png';
import AccountIcon from '../images/nav_blue_icon.png';
import '../features/navbar.css';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import { Link, useMatch, useResolvedPath } from "react-router-dom";

/*  Navbar() 
**  Purpose: Sets up the navbar for all main pages and puts objects inside 
**  Left Side: Logo (MLO Support)
**  Center: Menu - Dashboard, Leads, Borrowers, Lenders, Resources
**  Right: Dropdown Button - Account, Sign Out
*/

export default function Navbar() {
    return (

        <nav className="nav">
        <Link to="/dashboard" className="site-logo">
        <img
              src={BlueLogo} //MLO Support Logo
              width="150"
              height="60"
              className="d-inline-block align-top"
              alt="MLO Support"
            />
        </Link>

        <ul className="nav-menu">
            <SwitchPage to="/dashboard">Dashboard</SwitchPage>
            <SwitchPage to="/leads">Leads</SwitchPage>
            <SwitchPage to="/borrowers">Borrowers</SwitchPage>
            <SwitchPage to="/lenders">Lenders</SwitchPage>
            <SwitchPage to="/resources">Resources</SwitchPage>
        </ul>
            <BasicMenu />
        </nav>
    )
}

/* Checks for current page and underlines/bold it on the Navbar  */ 
function SwitchPage( {to, children, ...props }) {
    const resolvedPath = useResolvedPath(to)
    const isActive = useMatch( {path: resolvedPath.pathname, end:true} ) 

    return (
      // Checks the current page uses css to underline/bold the link on Navbar Menu
        <li className={ isActive ? "active" : ""}> 
            <Link to={to} {...props}>
                {children}
            </Link>
        </li>
    )
}


/* Dropdown button on navbar */
 function BasicMenu() {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
      setAnchorEl(null);
    };

    return (
      <div>
        <Button
          id="basic-button"
          aria-controls={open ? 'basic-menu' : undefined}
          aria-haspopup="true"
          aria-expanded={open ? 'true' : undefined}
          onClick={handleClick}
        >
             <img
              src={AccountIcon} // Blue Person Icon
              width="60"
              height="60"
              className="d-inline-block align-top"
              alt="MLO Support"
            />

        </Button>
        <Menu
          id="basic-menu"
          anchorEl={anchorEl}
          open={open}
          onClose={handleClose}
          MenuListProps={{
            'aria-labelledby': 'basic-button',
          }}
        >
          <MenuItem onClick={handleClose} className="btn-dropdown">
          <Link to="/account">Account</Link>
          </MenuItem>

          <MenuItem onClick={handleClose}>Logout</MenuItem>
        </Menu>
      </div>
    );
  }
